#[simd_test]
fn splat_f32x16<S: Simd>(simd: S) {
    let a = f32x16::splat(simd, 4.2);
    assert_eq!(
        *a,
        [
            4.2, 4.2, 4.2, 4.2, 4.2, 4.2, 4.2, 4.2, 4.2, 4.2, 4.2, 4.2, 4.2, 4.2, 4.2, 4.2
        ]
    );
}

